section {
  -primary-color: #2979FF;
  --color-text: #12232E;
  --hover-text-color: #000000;
  --ff-main: 'Roboto', sans-serif;
  --button-color: #154BF4;
  width: 100%;
  padding: 1.5rem 2rem;
  max-width: 1400px;
  margin: 0 auto;
  font-family: var(--ff-main);
  display: flex;
  place-items: center;
	nav {
    flex: 1;
		margin-left: 2rem;

    a {
      margin-left: 1rem;
    }
  }
  .login {

    a {
      margin-right: 14px;
    }

    button {
      background-color: var(--button-color);
      color: white;
      padding: 0.4rem 0.7rem;
      border-radius: 0.5rem;
      font-size: 18px;
    }
  }
}		
	a {
      color: var(--color-text);
      border-bottom: 2px solid transparent;
      transition: border-color 0.1s;
      font-size: 18px;
      font-weight: 700;
    }
	a:hover {
      border-bottom: 2px solid; 
      color: var(--hover-text-color);
      }

section {
  --primary-color: #2979FF;
  --color-text: #4B4356;
  --ff-main: 'Roboto', sans-serif;
  width: 100%;
  padding: 1.5rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
  font-family: var(--ff-main);
  display: flex;
  align-items: center;
  flex-direction: column;
	gap: 2rem;

  .logo {
    flex: 1;
    font-size: 2.44rem;
    line-height: .75rem;
    color: var(--primary-color);
    font-weight: 700;
  }

  nav {
    display: flex;
  	flex-direction: column;
    gap: 1rem;

    a {
      color: var(--color-text);
      border-bottom: 2px solid transparent;
      transition: border-color 0.1s;

      &:hover {
				border-color: var(--color-text);
      }

    }
  }
  button {
    display: flex;
    justify-content: center;

    svg {
      width: 24px;
      height: 24px;
    }
  }
}

@media (min-width: 700px) {
	section {
  	flex-direction: row;
		gap: 0;

    nav {
  		flex-direction: row;
    } 

  }
}

section {
  	width: 100%;
  	margin-right: auto;
    margin-left: auto;
  	padding: 1rem 2rem;
  	max-width: 1200px;
  	font-family: var(--ff-main);
  .content {
  		display: flex;
    	align-items: center;
  .logo {
    flex: 1;
      h1 {
        font-size: 2.44rem;
  			line-height: .75rem;
        color: var(--primary-color);
        font-weight: 700;
    }
  }
  nav {
      a {
        margin: auto 12px;
      }
      a:hover {
        border-bottom: 2px solid;
      }
  }
  button {
    cursor: pointer;
    .btn {
      position: relative;
    	top: 4px;
      svg {
      width: 24px;
      height: 24px;
    		}
    	}
  	}
	}
}
@media (max-width: 700px) {
  section .content h1 {
    display: grid;
    text-align: center;
		justify-content: center;
    height: 63px;
  }
  section .content nav {
    display: grid;
    position: relative;
    right: 50%;
    top: 60px;
    button {
      position: relative;
      left: 30%;
      a {
        justify-content: between;
      }
    }
  }
}
